const char htmlStart[]="<!DOCTYPE html><html lang='en'><head><meta charset='UTF-8'><meta name='viewport'content='width=device-width, initial-scale=1.0'><title>LED控制器</title><style>body{font-family:'Segoe UI',Tahoma,Geneva,Verdana,sans-serif;background-color:#f5f5f5;display:flex;align-items:center;justify-content:center;height:100vh;margin:0}.form-container{background-color:#fff;border-radius:8px;box-shadow:0 4px 8px rgba(0,0,0,0.1);overflow:hidden;width:300px;padding:20px;text-align:center}.form-title{font-size:20px;font-weight:bold;margin-bottom:20px}.form-input{width:100%;padding:10px;margin:10px 0;box-sizing:border-box;border:1px solid#ccc;border-radius:4px;transition:border-color 0.3s}.form-input:focus{border-color:#3498db;outline:none}.form-select{width:100%;padding:10px;margin:10px 0;box-sizing:border-box;border:1px solid#ccc;border-radius:4px}.form-button{background-color:#3498db;color:#fff;padding:10px 15px;border:none;border-radius:4px;cursor:pointer;font-size:16px;transition:background-color 0.3s}.form-button:hover{background-color:#2980b9}.loading-message{display:none;color:#3498db;margin-top:10px}</style></head><body><div class='form-container'><div class='form-title'>LED控制器配网</div><select name='custom_ssid'class='form-select'onchange='checkCustomSSID(this)'><option value=''disabled selected>选择或输入SSID</option>";
const char htmlEnd[]="<option value='custom'>自定义</option></select><form id='myForm'action='/save'method='post'onsubmit='showLoading()'><input type='text'name='ssid'class='form-input'placeholder='自定义SSID'style='display: none;'><input type='password'name='passwd'class='form-input'placeholder='密码'required><input type='text'name='uid'class='form-input'placeholder='UID'><input type='text'name='topic'class='form-input'placeholder='主题'><button type='submit'class='form-button'>提交</button><div class='loading-message'id='loadingMessage'>正在连接，请等待...</div></form><script>function checkCustomSSID(select){var customSSIDInput=document.getElementsByName('ssid')[0];if(select.value==='custom'){customSSIDInput.style.display='block';customSSIDInput.required=true}else{customSSIDInput.style.display='none';customSSIDInput.required=false;customSSIDInput.value=select.value}}function showLoading(){document.getElementById('loadingMessage').style.display='block'}</script></div></body></html>";
